使用Kruskal的思想（废话）
首先将所有水泥路加入生成树所有鹅卵石路加入生成树
这是加入生成树的鹅卵石都是必须加入的
在鹅卵石基础上再加入其他树
```cpp
#include <bits/stdc++.h>
using namespace std;
typedef pair<int, int> PII;
#define fi first
#define se second
#define MP make_pair
const int N = 200000;
struct data
{
	int a, b;
} a[N], b[N];

int f[N], n, m, K, lena, lenb, visa[N], visb[N];

int sf(int u)
{
	return f[u] == u ? u : f[u] = sf(f[u]);
}

int main()
{
	scanf("%d%d%d", &n, &m, &K);
	for (int i = 1; i <= m; i++)
	{
		int u, v, w;
		scanf("%d%d%d", &u, &v, &w);
		if (w == 0)
			b[++lenb] = (data){u, v};
		else
			a[++lena] = (data){u, v};
		// a 水泥路
		// b 鹅卵石路 
	}
	if (lenb < K)
		return puts("no solution"), 0;
	// 鹅卵石路不足K条 无解 
	for (int i = 1; i <= n; i++) f[i] = i;
	for (int i = 1; i <= lena; i++)
		if (sf(a[i].a) != sf(a[i].b))
			f[sf(a[i].a)] = sf(a[i].b);
	// 把水泥路加入生成树 
	int tmp = 0;
	for (int i = 1; i <= lenb; i++)
		if (sf(b[i].a) != sf(b[i].b))
		{
			visb[i] = 1;
			// visb[i] 表示鹅卵石路i是否被取 
			tmp++;
			// tmp 已经取的鹅卵石路数量 
			f[sf(b[i].a)] = sf(b[i].b);
		}
	if (tmp > K)
		return puts("no solution"), 0;
	// 如果必须要取的鹅卵石路超过K条 无解 
	for (int i = 1; i < n; i++)
		if (sf(i) != sf(i + 1))
			return puts("no solution"), 0;
	// 如果加入所有边，图还不联通，无解 
	for (int i = 1; i <= n; i++) f[i] = i;
	for (int i = 1; i <= lenb; i++)
		if (visb[i])
			f[sf(b[i].a)] = sf(b[i].b);
	// 把所有必须取的鹅卵石路加入生成树 
	for (int i = 1; i <= lenb && tmp < K; i++)
		if (!visb[i])
			if (sf(b[i].a) != sf(b[i].b))
			{
				visb[i] = 1;
				tmp++;
				// tmp 已经取的鹅卵石路数量 
				f[sf(b[i].a)] = sf(b[i].b);
			}
	// 把鹅卵石路加到K条为止 
	if (tmp < K)
		return puts("no solution"), 0;
	for (int i = 1; i <= lena; i++)
		if (sf(a[i].a) != sf(a[i].b))
		{
			visa[i] = 1;
			f[sf(a[i].a)] = sf(a[i].b);
		}
	// 加入水泥路，使整个图联通 
	for (int i = 1; i <= lena; i++)
		if (visa[i])
			printf("%d %d %d\n", a[i].a, a[i].b, 1);
	for (int i = 1; i <= lenb; i++)
		if (visb[i])
			printf("%d %d %d\n", b[i].a, b[i].b, 0);
}
