        }
    }
    for (int i=k+1;i<=m-k+1;i++) {
        for (int j=k;j<=n-k;j++) {
            ans=max(ans,dp[n][i][3]+dp[j][i-1][4]+dp[j+1][i-1][2]);
        }
    }
    for (int i=k;i<=n-2*k;i++) {
        ans=max(ans,dp[i][m][4]+calc2[i+k]+dp[i+k+1][1][1]);
    }
    for (int i=k;i<=n-2*k;i++) {
        for (int j=k;j<=m-k;j++) {
            ans=max(ans,dp[i][m][4]+dp[i+1][j][2]+dp[i+1][j+1][1]);
