void csh(){//计算
    for (int i=n-k+1;i;i--) {
        for (int j=m-k+1;j;j--) dp[i][j][1]=max(dp[i+1][j][1],max(dp[i][j+1][1],max(dp[i+1][j+1][1],s(i+k-1,j+k-1))));//左上
        for (int j=k;j<=m;j++) dp[i][j][2]=max(dp[i+1][j][2],max(dp[i][j-1][2],max(dp[i+1][j-1][2],s(i+k-1,j))));//右上
