## 完整代码-C++
```cpp
#include <bits/stdc++.h>
#define int long long
using namespace std;
int n, m, k, a[1705][1705], b[1705][1705], c[1705][1705], d[1705][1705], s[1705][1705];
signed main(){
    ios::sync_with_stdio(0);
    cin >> n >> m >> k;
    for(int i = 1;i<=n;i++){
        for(int j = 1;j<=m;j++){
            int x;
            cin >> x;
            s[i][j] = s[i-1][j]+s[i][j-1]-s[i-1][j-1]+x;
        }
    }
    //初始化abcds
    for(int i = n;i>=k;i--){
        for(int j = m;j>=k;j--){
            s[i][j]-=s[i-k][j]+s[i][j-k]-s[i-k][j-k];
        }
    }
    for(int i = k;i<=n;i++){
        for(int j = k;j<=m;j++){
            a[i][j] = max(s[i][j], max(a[i-1][j], a[i][j-1]));
        }
    }
    for(int i = k;i<=n;i++){
        for(int j = m;j>=k;j--){
            b[i][j] = max(s[i][j], max(b[i-1][j], b[i][j+1]));
        }
    }
    for(int i = n;i>=k;i--){
        for(int j = k;j<=m;j++){
            c[i][j] = max(s[i][j], max(c[i+1][j], c[i][j-1]));
        }
    }
    for(int i = n;i>=k;i--){
        for(int j = m;j>=k;j--){
            d[i][j] = max(s[i][j], max(d[i+1][j], d[i][j+1]));
        }
    }
    //递推求值
    int ans = 0;
    for(int i = k;i<=n-k;i++){
        for(int j = k;j<=m-k;j++){
            ans = max(ans, a[i][j]+b[i][j+k]+c[i+k][m]);
        }
    }
    for(int i = k+k;i<=n;i++){
        for(int j = k;j<=m-k;j++){
            ans = max(ans, c[i][j]+d[i][j+k]+a[i-k][m]);
        }
    }
    for(int i = k+k;i<=n-k;i++){
        for(int j = k;j<=m;j++){
            ans = max(ans, s[i][j]+a[i-k][m]+c[i+k][m]);
        }
    }
    for(int i = k;i<=n-k;i++){
        for(int j = k;j<=m-k;j++){
            ans = max(ans, a[i][j]+c[i+k][j]+b[n][j+k]);
        }
    }
    for(int i = k;i<=n-k;i++){
        for(int j = k+k;j<=m;j++){
            ans = max(ans, a[n][j-k]+b[i][j]+d[i+k][j]);
        }
    }
    for(int i = k;i<=n-k;i++){
        for(int j = k+k;j<=m-k;j++){
            ans = max(ans, s[i][j]+a[n][j-k]+b[n][j+k]);
        }
    }
    cout << ans;
    return 0;
}
