pad calc_l(const int &n, const ll &ans, const int &dig){
	memset(cnt_div, -1, sizeof(cnt_div));
	dp[0] =0, cnt_div[0] =0;
	ll tmp_ans =((~ans)^(pow_2[dig]-1));
	for(int i =1; i <= n; ++i){
