			memset(dp, 0, sizeof(dp));
			dp[0][0] = 1;
			int tot = ans | (k - 1);
			bool flag = 0;
			for (int i = 1; i <= n; ++i) {
				for (int j = 1; j <= min(i, r); ++j) {
					for (int p = 0; p < i; ++p) {
						if (((s[i] - s[p]) | tot) == tot && dp[p][j - 1]) {
							dp[i][j] = 1;
						}
					}
				}
			}
			for (int i = l; i <= r; ++i) {
				if (dp[n][i]) {
					flag = 1; break;
				}
			}
			if (!flag) ans |= k;
		}
	} else {
		for (int k = (1ll << 45); k > 0; k >>= 1) {
			memset(f, 0x3f, sizeof(f));
			f[0] = 0;
			int tot = ans | (k - 1);
			for (int i = 1; i <= n; ++i) {
				for (int j = 0; j < i; ++j) {
