	while(ch<='9'&&ch>='0'){re=re*10+ch-48;ch=getchar();}
	return re*k;
}

il void write(int x)
{
	if(x<0)return write(-x),void();
	if(x<10)return putchar(x+48),void();
	return write(x/10),write(x%10),void();
