{
	int t=0;ans=max(ans,f[x][0]+g[x][0]);
	for(int i=la[x],v;i;i=edge[i].nx)
		if((v=edge[i].to)!=fa)
			g[v][0]=max(g[x][0]+dp[x][0][t],max(g[x][1]+dp[x][0][t],g[x][0]+dp[x][1][t])+edge[i].val),g[v][1]=g[x][0]+dp[x][0][t]+edge[i].val,dfs2(v,x),++t;
