	for(int w=0;w+j<=n;w++)
	{
		if(init(i,j,q,w))
			s[++cnt].l=i,s[cnt].r=j,s[cnt].ll=i+q,s[cnt].rr=j+w;
	}
	memset(isok,1,sizeof(isok));
	for(int i=1;i<=cnt;i++)
	{
		if(isok[i]==0)	continue;
		for(int j=1;j<=cnt;j++)
		{
			if(i==j)	continue;
			if(s[i].l<=s[j].l&&s[i].ll>=s[j].ll&&s[i].r<=s[j].r&&s[i].rr>=s[j].rr)
