			int ny=y+dy[i];
			if(nx<n1 || nx>n2 || ny<m1 || ny>m2 || v[nx][ny] || a[nx][ny]!=a[x][y]) continue;
			v[nx][ny]=true;
			dfs(nx,ny,n1,n2,m1,m2);
		}
}
bool ppcl(int u1,int v1,int u2,int v2){
