						c1=a[i][j];
					if(col==2)
						c2=a[i][j];
				}
	if(col!=2)
		return false;
	memset(sum,0,sizeof(sum));
	memset(v,0,sizeof(v));
	for(int i=u1;i<=u2;i++)
		for(int j=v1;j<=v2;j++)
			{
				if(!v[i][j])
					{
						v[i][j]=true;
						sum[a[i][j]]++;
