		for(int j=1;j<=n;j++)
			for(int k=i;k<=n;k++)
				for(int l=j;l<=n;l++)
					if(ppcl(i,j,k,l))
						{
							cnt++;
							s[cnt].x1=i;
							s[cnt].y1=j;
							s[cnt].x2=k;
							s[cnt].y2=l;
