                                sum[map[k][l]]++;
                                bfs(k , l);
                            }
                        }
                    if(find(sum)) {
                        top++;
                        pcl[top].x1 = x;
                        pcl[top].y1 = y;
                        pcl[top].x2 = x + len1 - 1;
                        pcl[top].y2 = y + len2 - 1;
                    }
                }
        }
    for(int i=1; i<=top; i++)
        if(Ans(i))
