        sum[i][a[i]]++;//求每个乐队人数的前缀和
    }
    memset(f,0x3f,sizeof(f));//初始化最大值
    f[0] = 0;//一个乐队都没有的时候取0人
    for(int i=1;i<(1<<m);++i){
