**code**

```cpp
#include <bits/stdc++.h>
using namespace std;

//&Start
#define lng long long
#define lit long double
#define kk(i,n) " \n"[i==n]
const int inf=0x3f3f3f3f;
const lng Inf=0x3f3f3f3f3f3f3f3f;

//&Data
const int N=1e6,mod=1e9+7;
int t,n,m;

//&Pow
int Pow(int a,int x){
	int res=1;
	for(;x;a=1ll*a*a%mod,x>>=1)
		if(x&1) res=1ll*res*a%mod;
	return res;
}

//&Mobius&Fibonacci
bitset<N+10> np;
int p[N+10],cnt,mu[N+10],tp[N+10],f[N+10],g[N+10],h[N+10];
void Mobius(){
	np[1]=true;
	mu[1]=f[1]=g[1]=h[1]=1;
	for(int i=2;i<=N;i++){
		if(!np[i]) p[++cnt]=i,mu[i]=-1;
		f[i]=(f[i-1]+f[i-2])%mod;
		g[i]=Pow(f[i],mod-2);
		h[i]=1;
		for(int j=1;j<=cnt&&i*p[j]<=N;j++){
			np[i*p[j]]=1;
			if(i%p[j]==0){mu[i*p[j]]=0;break;}
			mu[i*p[j]]=-mu[i];
		}
	}
	for(int k=1;k<=N;k++){ //nlogn 的暴力筛
		if(mu[k]==0) continue;
		for(int T=k;T<=N;T+=k)
			h[T]=1ll*h[T]*(mu[k]==1?f[T/k]:g[T/k])%mod;
	}
	h[0]=1;
	for(int i=1;i<=N;i++) h[i]=1ll*h[i-1]*h[i]%mod;//前缀积
}

//&Main
int main(){
	Mobius();//这东西千万不能忘记打
	scanf("%d",&t);
	for(int ti=1;ti<=t;ti++){
		scanf("%d%d",&n,&m);
		if(n>m) n^=m^=n^=m;
		int res=1,a,x;
		for(int l=1,r;l<=n;l=r+1){
			r=min(n/(n/l),m/(m/l));
			a=1ll*h[r]*Pow(h[l-1],mod-2)%mod;
			x=1ll*(n/l)*(m/l)%(mod-1);//幂次一定一定一定要模(phi(mod))
			res=1ll*res*Pow(a,x)%mod;
		}
		printf("%d\n",res);
	}
	return 0;
}
