<summary>AC_code</summary>
<code>

```cpp
#include<bits/stdc++.h>
using namespace std;
#define re register int
#define ull unsigned long long
#define double long double
const int N=305;
int n,m;
char ch[N][N];
ull hs[N][N],bas=131,ba[N];
double a[N][N],x[N],mse[N];
signed main(){
	scanf("%d%d",&n,&m);
	ba[0]=1;mse[0]=1;
	for(re i=1;i<=m;i++)
		ba[i]=ba[i-1]*bas,
		mse[i]=mse[i-1]/2.0;
	for(re i=1;i<=n;i++){
		scanf("%s",ch[i]+1);
		for(re j=1;j<=m;j++)
			hs[i][j]=hs[i][j-1]*bas+ch[i][j];
	}
	for(re i=1;i<=n;i++){
		for(re j=1;j<=n;j++){
			double ans=0;
			for(re k=0;k<=m;k++){
				ull tmpf=hs[i][k];
