#include<algorithm>
#include<cstdio>
#define MaxN 2005000
#define ll long long
using namespace std;
void exgcd(ll a,ll b,ll &x,ll &y){
  if (b==0){x=1;y=0;return ;}
  exgcd(b,a%b,y,x);y-=(a/b)*x;
}
ll inv(ll a,ll m){
  ll x,y;exgcd(a,m,x,y);
  return (x%m+m)%m;
}
ll powM(ll a,ll t,int mod)
{
  ll ret=1;
  while(t){
    if (t&1)ret=ret*a%mod;
    a=a*a%mod;t>>=1;
  }return ret;
}
struct Data
{
  int p,mod;
  ll sav[MaxN];
  void Init(){
    sav[0]=1;
    for (int i=0;i<mod;i+=p){
      for (int j=i+1;j<i+p;j++)
        sav[j]=sav[j-1]*j%mod;
      sav[i+p]=sav[i+p-1];
    }
  }
  ll v(ll n){
    ll ret=0;
    while(n)ret+=(n/=p);
    return ret;
  }
  ll r(ll n){
    if (n==0)return 1;
    return powM(sav[mod-1],n/mod,mod)*sav[n%mod]%mod*r(n/p)%mod;
  }
  ll C(ll n,ll m)
  {
    ll c=v(n)-v(m)-v(n-m),
       ans=powM(p,c,mod);
    if (!ans)return 0;
    ans=ans*r(n)%mod;
    ans=ans*inv(r(m),mod)%mod;
    ans=ans*inv(r(n-m),mod)%mod;
    return ans;
  }
  ll calc(ll a,ll b)
  {
    if (a==b)return (powM(2,a+b,mod)-C(a<<1,a)+mod)%mod;
    ll ret=0;
    for (int i=1;i<a-b;i++)
      ret+=C(a+b,b+i);
    return (powM(2,a+b,mod)+ret)%mod;
  }
}T2,T5;
ll a,b;int k;
void solve()
{
  T2.mod=2;for (int i=0;i<k;i++)T2.mod*=2;
  T5.mod=1;for (int i=0;i<k;i++)T5.mod*=5;
  T2.Init();T5.Init();
  int mod=T2.mod*T5.mod;
  ll ret=(T2.calc(a,b)*inv(T5.mod,T2.mod)%mod*T5.mod
         +T5.calc(a,b)*inv(T2.mod,T5.mod)%mod*T2.mod)%mod;
