	ll tb = 0;
	dfs2(n >> 1, ta, tb, xk);
	a = 1ll * a * ta % md;
	b += tb + (n >> 1);
	if (n >= xk) a = 1ll * a * ksm(j2[xk - 1], n / xk) % md;
	a = 1ll * a * j2[n - (n / xk) * xk] % md;
}
void dfs5(ll n, int & a, ll & b, int xk) {
	if (n == 0) return;
