    int p1 = lucas(n, md), p2 = lucas(m, md), p3 = lucas(n - m, md);
    return qpow(bs[md], d, mod[md]) * p1 % mod[md] *  inv(p2, mod[md]) % mod[md] * inv(p3, mod[md]) % mod[md];
}
