	void Init()
	{
		memset(cnt,0,sizeof(cnt)),memset(num,0,sizeof(num));
		int now=1;for(int i=1;i<=n;i+=kc)L[now]=i,R[now]=min(i+kc-1,n),now++;
	}
	void ins(int x){int p=getid(x);num[x]++;cnt[p]++;}
	void del(int x){int p=getid(x);num[x]--;cnt[p]--;}
