    double l=0.0,r=1e10*1.0;
    while(r-l>1e-4){
        double mid=(l+r)/2;
        if(check(mid))l=mid+0.00001;
        else r=mid-0.00001;
    }
    printf("%.10lf",l);
    return 0;
}





