#include<iostream>
#include<cstdio>
#define ni(x) power(x,md-2)
using namespace std;

char rc;
void read(long long &x)
{
    x=0;rc=getchar();
    while(rc<'0'||rc>'9')
      rc=getchar();
    while(rc>='0'&&rc<='9')
      x=x*10+rc-'0',rc=getchar();
}

long long n,m,md,t,T,i,ans;//md就是题目的p 

long long power(long long a,long long n)
{
    long long ans=1;
    while(n)
    {
        if(n&1)
          ans=(ans*a)%md;
        a=(a*a)%md;
        n>>=1;
    }
    return ans;
}

int cnt;
long long check(long long x,long long detla)//除掉因子p 
{
    while(x%md==0)
      x/=md,cnt+=detla;//算分子的时候detle每次+1，分母每次-1 
    return x;
}

int main()
{
    cin>>t;
    for(T=1;T<=t;T++)
    {
        cin>>n>>m>>md;cnt=0;
        ans=1;
        for(i=n+1;i<=n+m;i++)
          ans=(ans*check(i,1))%md;
        for(i=2;i<=m;i++)
          ans=(ans*ni(check(i,-1)))%md;
        cout<<(cnt==0?ans:0)<<endl;
    }
    return 0;
}
```
