然后。快速幂咯



```cpp
#include<cstdio>
#include<iostream>
using namespace std;
int pri[200010],cut;
int in[200010];
int ppr[200010];
int n,m;
long long p;
int maxn;
long long sumn=1;
int T;
void fen1(int t){
    int tip=1;
    while(t!=1){
        if(t%pri[tip]==0){
            while(t%pri[tip]==0){
                t/=pri[tip];
                ppr[tip]++;
            }
            maxn=max(maxn,tip);
        }
        tip++;
    }
}
void fen2(int t){
    int tip=1;
    while(t!=1){
        if(t%pri[tip]==0){
            while(t%pri[tip]==0){
                t/=pri[tip];
                ppr[tip]--;
            }
            maxn=max(maxn,tip);
        }
        tip++;
    }
}
long long ksm(long long u,int v,long long p){
    long long re=1;
    for(;v;u=((u%p)*(u%p))%p,v>>=1){
        if(v&1){
            re=((re%p)*(u%p))%p;
        }
    }
    return re;
}
int main(){
    cin>>T;
    for(int i=2;i<=200000;i++){
        if(in[i]==0){
            pri[++cut]=i;
        }
        for(int j=1;j<=cut;j++){
            if(i*pri[j]>200000){
                break;
            }
            in[i*pri[j]]=1;
            if(i%pri[j]==0) break;
        }
    }
    while(T--){
        cin>>n>>m>>p;
        for(int i=1;i<=maxn;i++){
            ppr[i]=0;
        }
        maxn=0;
        sumn=1;
        for(int i=n+m;i>n;i--){
               fen1(i);
           }
           for(int i=m;i>=1;i--){
            fen2(i);
        }
        for(int i=1;i<=maxn;i++){
               sumn=((sumn%p)*(long long)ksm((long long)pri[i],ppr[i],p))%p;
        }
        cout<<sumn<<endl;
    }
    return 0;
}
