#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const ll N = 55, bit = 50;

ll n, a, p[N];
void insert(ll k) {
	for(ll i=bit;i>=0;i--) {
		if(!(k&(1LL<<i))) continue;
		if(!p[i]) return p[i] = k, void();
		k ^= p[i];
	}
}
ll maxXor() {
	ll res = 0;
	for(ll i=bit;i>=0;i--) res = max(res, (res^p[i]));
	return res;
}

int main() {
	scanf("%lld", &n);
	while(n--) {scanf("%lld", &a); insert(a);}
	printf("%lld\n", maxXor());
	return 0;
}
