```cpp
#include <cstdio>

typedef long long LL;

const int MAXN = 55, MAXLOG = 55;

template<typename _T>
void read( _T &x )
{
	x = 0;char s = getchar();int f = 1;
	while( s > '9' || s < '0' ){if( s == '-' ) f = -1; s = getchar();}
	while( s >= '0' && s <= '9' ){x = ( x << 3 ) + ( x << 1 ) + ( s - '0' ), s = getchar();}
	x *= f;
}

template<typename _T>
void write( _T x )
{
	if( x < 0 ){ putchar( '-' ); x = ( ~ x ) + 1; }
	if( 9 < x ){ write( x / 10 ); }
	putchar( x % 10 + '0' );
}

template<typename _T>
_T MAX( const _T a, const _T b )
{
	return a > b ? a : b;
}

LL P[MAXLOG];
LL S;
int N;

void insert( LL val )
{
	for( int i = 50 ; ~ i ; i -- )
	{
		if( val & ( 1ll << i ) )
		{
			if( ! P[i] ) 
			{
				P[i] = val;
				break;
			}
			val ^= P[i];
		}
	}
}

LL solve()
{
	LL res = 0;
	for( int i = 50 ; ~ i ; i -- )
	{
		res = MAX( res, res ^ P[i] );
	}
	return res;
}

int main()
{
	read( N );
	for( int i = 1 ; i <= N ; i ++ )
	{
		read( S );
		insert( S );
	}
	write( solve() ), putchar( '\n' );
	return 0;
}
```

