int abs(int x)
{
	if(x<0)return -x;
	return x;
}
int min(int x,int y)
{
	if(x<y)return x;
	return y;
}
int L[20005],R[20005];
int main()
{
	int n;
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		scanf("%d%d",&L[i],&R[i]);
	}
	f[1][0]=L[1]-1+(R[1]-L[1])*2;
	f[1][1]=R[1]-1; 
	for(int i=2;i<=n;i++)
	{
		f[i][0]=min(f[i-1][1]+abs(R[i-1]-R[i]),f[i-1][0]+abs(L[i-1]-R[i]))+R[i]-L[i]+1;
		f[i][1]=min(f[i-1][1]+abs(R[i-1]-L[i]),f[i-1][0]+abs(L[i]-L[i-1]))+R[i]-L[i]+1;
	}
	printf("%d\n",min(f[n][0]+n-L[n],f[n][1]+n-R[n]));
	return 0;
}
