好CODE：
```cpp
#include<bits/stdc++.h>
using namespace std;
const int MAXN=3000+10;
int n,a[MAXN],dp[MAXN][MAXN];
int dfs(int l,int r){
	if(l==r) return 0;
	if(dp[l][r]!=0x3f3f3f3f) return dp[l][r];
	if(r-l==1){
		if(a[r]==a[l]) return 0;
		else return 1;
	}
	if(a[l]==a[r]){
		dp[l][r]=min(dp[l][r],dfs(l+1,r-1));
	}
	else{
		dp[l][r]=min(min(dp[l][r],dfs(l+1,r-1)+1),min(dfs(l+1,r)+1,dfs(l,r-1)+1));
	}
	return dp[l][r];
}
int main(){
	memset(dp,0x3f,sizeof(dp));
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%d",&a[i]);
	}
	printf("%d\n",dfs(1,n));
	return 0;
}
```

