#include<iostream>
#include<cstdio>
#include<cmath>
#include<algorithm>
#define int long long 
using namespace std;
const int N=59;
const int mod=2008;
int light[N];
int p[N];
int n,m;
int ans;
int cont;
void calc(string x,int num)
{
	int len=x.length();
	int cnt=0;
	for(int i=len-1;i>=0;i--)
	{
		if(x[i]=='O')
			light[num]+=(1ll<<cnt);
		cnt++;
	}
	return;	
}
void linear_basis(int x)
{
	for(int i=55;i>=0;i--)
	{
		if(!(x>>i)) continue;
		if(!p[i])
		{
			p[i]=x;
			cont++;
			return;
		} 
		x^=p[i];
	}
}
signed main()
{
	scanf("%lld%lld",&n,&m);
	for(int i=1;i<=m;i++)
	{
		string x;
		cin>>x;
		calc(x,i);
	}
	for(int i=1;i<=m;i++)
		linear_basis(light[i]);
	printf("%lld",(1ll<<cont)%mod);
	return 0; 
}

