#include<cmath>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
const int mod=2008; 
int n,m;
char num[100][100];
long long int a[100]={0},b[100];
int fpow(int a,int b)
{
	int ans=1;
	for (;b;a=(a*a)%mod,b>>=1)
		if (b&1) ans=(ans*a)%mod; 
	return ans;
}
int main()
{
	scanf("%d%d",&n,&m);
	for (int i=1;i<=m;i++) scanf("%s",num[i]+1);
	for (int i=1;i<=m;i++)
		for (int k=1;k<=n;k++)
			if (num[i][k]=='O') a[i]=(a[i]<<1)|1;
				else a[i]=a[i]<<1+0;
	for (int i=1;i<=m;i++)
	for (int k=n;~k;k--)
	if (a[i]&(1LL<<k)) 
		if (!b[k]){b[k]=a[i];break;}
			else a[i]^=b[k];
	int ans=0;
	for (int i=n;~i;i--)
		if (b[i]) ans++;
	cout<<fpow(2,ans);
	return 0;
}
```
