queue<node>q;

inline bool check(int x,int y,int st) //判断机关状态为st时点(x, y)是不是墙 
{
	bool ret=a[x][y];
	for(int i=1;i<=k;i++)
	{
		if(R[i]==x&&C[i]==y)
			ret ^= bool(st & (1<<i-1));
	}
	return ret;
}

inline int newStat(int x,int y,int st) //求出机关状态为st时走到了点(x, y)后机关的新状态 
{
	int ret = st;
	for(int i=1;i<=k;i++)
	{
		if(r[i]==x&&c[i]==y)
			ret ^= 1<<i-1;
	}
	return ret;
