			if(a[i]>='a' && a[i]<='z' || a[i]>='A' && a[i]<='Z')//如果是字母
			{
				if(nnn>=n)//如果第一个提示输出完了
				{
					if(a[i]=='A' || a[i]=='E' || a[i]=='I' || a[i]=='O' || a[i]=='U' || a[i]=='a' || a[i]=='e' || a[i]=='i' || a[i]=='o' || a[i]=='u')//判断是否是元音字母
						cout << a[i];//输出元音字母
					else
						cout << '.';//不提示
				}
				else
					cout << a[i],nnn++;//又输出了一个字母
			}
			else
				cout << a[i];//输出符号
	}
	cout << endl;
	return 0;
}
