#include<iostream>
#include<cstdio>
using namespace std;
struct node{
    int v;
    int t;
}e[210];//邻接表存树，由于是双向边，所以数组开到2倍
int a1,a2,s,n,q,f[110],w[110],d[110],a[8][110];//a[j][i]表示从第i个点向上跳(i<<j)个单位（即2^j个单位）后到达的点
//f数组存储树
//w[i]表示深度为i的点的个数
//d[i]表示第i个点的深度
int fr(){
    char c=getchar();
    while(c>'9'||c<'0')
        c=getchar();
    int sum=c-'0';
    while((c=getchar())<='9'&&c>='0')
        sum=sum*10+c-'0';
    return sum;
}//快读
void add(int x,int y){
    s++;
    e[s].v=y;
    e[s].t=f[x];
    f[x]=s;
}//加边函数
void dfs(int u,int p){
    d[u]=d[p]+1;
    a1=max(a1,d[u]);//更新树的深度
    for(int i=1;i<=7;i++)
        a[i][u]=a[i-1][a[i-1][u]];//递推求出该路径上点的LCA
    int x;
    for(int i=f[u];i!=0;i=e[i].t){
        x=e[i].v;
        if(x==p)
            continue;//如果指向节点为父节点，为了避免死循环，continue。
        a[0][x]=u;
        dfs(x,u);//搜索子节点
    }
}//DFS算出每个点的深度
int lca(int x,int y){
    if(d[x]<d[y])
        swap(x,y);//默认x深度大向上跳
    int h=d[x]-d[y];//向上跳的深度
    for(int i=7;i>=0;i--)
        if(d[a[i][x]]>=d[y])
            x=a[i][x];//直到深度相同为止
    if(x==y)
        return x;//如果此时节点相同，那么直接返回该节点
    for(int i=7;i>=0;i--)
        if(a[i][x]!=a[i][y]){
            x=a[i][x];
            y=a[i][y];
        }//一起向上跳，直到父节点相同（如果把节点相同作为判断条件，所得点不一定是最近的公共祖先）
    return a[0][x];//返回父节点
}
int query(int x,int y){
    return (d[x]-d[lca(x,y)])*2+d[y]-d[lca(x,y)];
}//根据公式查询距离
int main(){
    int x,y;
    n=fr();
    for(int i=1;i<=n-1;i++){
        x=fr();
        y=fr();
        add(x,y);
        add(y,x);//正反都建边
    }
    dfs(1,0);
    cout<<a1<<endl;//深度
    for(int i=1;i<=n;i++){
        w[d[i]]++;
        a2=max(a2,w[d[i]]);//宽度
    }
    cout<<a2<<endl;
    x=fr();
    y=fr();
    cout<<query(x,y);//距离
    return 0;
}
