     for(int i=1;i<n;i++)
     {
          int a,b;
          scanf("%d %d",&a,&b);
           addedge(a,b);
      addedge(b,a);
      }
      scanf("%d %d",&p,&q);
      depth[1]=1;
      dist[1]=0;
      tree(1);
    printf("%d\n",maxn);
    int wide=0;
     for(int i=1;i<=maxn;i++)
     {
         int ans=0;
         int t=i;
          for(int j=1;j<=n;j++)
          {
              if(depth[j]==t)
               ans++;
          }
          wide=max(ans,wide);
     }
