        sum+=k.top();k.pop();//与queue不同的是弹出操作是top()，不是front()
        for (int j=1;j<=a;j++){
            sum+=d.top();d.pop();
        }
        for (int j=1;j<=b;j++){
            sum+=m.top();m.pop();
        }
        for (int j=1;j<=c;j++){
            sum+=f.top();f.pop();
        }
        printf("%.2f\n",(double)sum/11.0);//计算平均值并输出
    }
}
