			//枚举资源
			//	printf("%d %d\n",dp[i][j]);
				if(dp[i][j]==-1)continue;

				for(re k=0;k<=j;k++){
				//枚举花多少买苦力
					if(f[k]==0)continue;
					int w=(j-k)+f[k]+dp[i][j];
