int n,a[10000000],b[10000000]={0},maxn,zmax;
int main()
{
    cin>>n;
    for(int i=0;i<n;i++)
        cin>>a[i];
    b[n-1]=0;
    for(int i=n-1;i>=0;i--)
    {
        maxn=-1;
        for(int j=i+1;j<n;j++)
            if(b[j]>=maxn&&a[i]<=a[j]) maxn=b[j];
        if(maxn!=-1) b[i]=maxn+1;
        else b[i]=1;
    }
    zmax=-1;
    for(int i=0;i<n;i++)
        if(b[i]>zmax) zmax=b[i];
    cout<<n-zmax<<endl;
    return 0;
}
