#include<iostream>
#include<cstdio>
using namespace std;
typedef long long ll;
ll n,ans=0;
ll a[10001],f[10001][3];
void fir()
{
	scanf("%lld",&n);
	for(ll i=1;i<=n;++i)scanf("%lld",&a[i]);

}
void work()
{
	for(ll i=1;i<=n;++i)
	{
		f[i][0]=f[i][1]=1;
		for(ll j=1;j<i;++j)
		{
			if(a[j]>a[i])f[i][1]=max(f[i][1],f[j][0]+1);
			if(a[j]<a[i])f[i][0]=max(f[i][0],f[j][1]+1);
		}
	}
}
