ll ans;     

il int check(int se[], int xx){   
	rg l = 1, r = m;    
	while(l < r - 1){     
		if(xx < se[mid]) r = mid - 1;  
		el(xx > se[mid]) l = mid + 1;  
		else r = mid;    
	}    
	if(se[l] == xx) return l;    
	return r;    
}     

