#include<bits/stdc++.h>

using namespace std;

const int N = 3e5;
const int inf = 1e9;

int s = 1e5, t = s + 1, tmp = 1, n, m, sum = 0;

struct dt{
    int from, to, f, next;
}e[N];

int head[N], dep[N], cur[N];

bool vis[N];

string s1[N], s2[N];

void add(int a, int b, int val){
    e[++tmp].from = a;
    e[tmp].to = b;
    e[tmp].f = val;
    e[tmp].next = head[a];
    head[a] = tmp;
    e[++tmp].from = b;
    e[tmp].to = a;
    e[tmp].f = 0;
    e[tmp].next = head[b];
    head[b] = tmp;
}

bool bfs(){
	memset(dep, 0x3f, sizeof(dep));
	memset(vis, 0, sizeof(vis));
	for(int i = 1; i <= N; i++)cur[i] = head[i];
	queue<int> q;
	vis[s] = 1;
	dep[s] = 0;
	q.push(s);
	while(!q.empty()){
		register int u = q.front();
		q.pop();
		for(register int i = head[u]; i; i = e[i].next){
			register int v = e[i].to;
			if(dep[v] > dep[u] + 1 && e[i].f){
				dep[v] = dep[u] + 1;
				if(!vis[v])
					q.push(v);
				vis[v] = 1;
			}
		}
	}
	return dep[t] != 0x3f3f3f3f;
}

int dfs(int now, int low){
	int flow = 0;
	if(now == t)return low;
	for(register int i = cur[now]; i; i = e[i].next){
		cur[now] = i;
		int v = e[i].to;
		if(e[i].f && dep[v] == dep[now] + 1){
			if(flow = dfs(v, min(low, e[i].f))){
				e[i].f -= flow;
				e[i ^ 1].f += flow;
				return flow;
			}
		}
	}
	return 0;
}

int dinic(){
	int minflow, maxflow = 0;
	while(bfs()){
		while(minflow = dfs(s, inf))maxflow += minflow;
	}
	return maxflow;
}

