long long check(int x, int l, int r,bool p) {
	if(l > r) return 0;//区间不存在
	long long x1 = ((as[r] - as[l-1]) % M + M) % M;
	x1 = x1 * ds[x] % M; //区间各值到x的价值
	long long x2 = ((cnt[r] - cnt[l-1]) % M + M) % M;//区间总数个的x价值
	if(!p) 
   	return ((x2 - x1) % M + M) % M;
    else
		return ((x1 - x2) % M + M) % M;
}
