			pri[++tot]=i;
			phi[i]=i-1;
		}
		for(int j=1;j<=tot && i*pri[j]<=MAXN;j++)
		{
			isp[i*pri[j]]=1;
			if(i%pri[j]) phi[i*pri[j]]=phi[i]*(pri[j]-1);
			else
			{
				phi[i*pri[j]]=phi[i]*pri[j];
