void Mod(int &x,int p,bool &fg)
{
	if(x>=p)
	{
		x%=p;
		fg=1;
	}
}
int QuickPow(int x,int y,int p)
{
	int res=1;
	bool fg=0;
	Mod(x,p,fg);
	while(y)
	{
		if(y&1)
		{
			res=res*x;
			Mod(res,p,fg);
		}
		x=x*x;
		Mod(x,p,fg);
		y>>=1;
	}
	return res+fg*p;
}
