```cpp
#include<iostream>
#include<cstdio>
using namespace std;
int m;
long long fib[10002]={0};//存每个fib的值
void qfib()//把数组的每个值求出来
{
    fib[1]=1;fib[2]=1;
    for(int i=3;i<=10002;i++)
     fib[i]=fib[i-1]+fib[i-2];
}
int main()
{
    cin>>m;
    long long a,b;//因为是fib数列，所以数据可能很大开longlong 
    for(int i=1;i<=m;i++)
    {
        scanf("%I64d%I64d",&a,&b);
        if(a==b)
        {
            cout<<a<<endl;//如果两个数一样，那么就输出原来的
            continue;
        }
        for(int i=60;i>=0;i--)//个人感念得翻着推
        {
            if(a>fib[i])a-=fib[i];
            if(b>fib[i])b-=fib[i];
            if(a==b){
                printf("%d\n",a);最后的输出
                break;
            }
        }
    }
    return 0;//结束
}
