LL lca(LL x,LL y){            
    if(x == 1 || y == 1)    return 1;        //特判的话可以骗分。。 
    if(x == y+1 || y == x+1) return 1;
    int depx,depy;
    depx = ef(x);        //得到这个点的辈分（深度） 
    depy = ef(y);
    while(1){
