                    depy--;
            }
            while(depx>depy && x!=1){
                x -= f[depx];
                while(depx>0 && f[depx] >= x)
                    depx--;
            }
            if(x == y)        // 如果这里已经找到了就返回这个值 
                return x;
        }
        while(x != y){            //如果两个是同一辈的，就一起往上跳 
            if(x!=1){        // 1不用跳。。。 
                x = x-f[depx];
                while(depx>0 && f[depx] >= x)    
                    depx--;    
