int h[100001],deep[100001],rt[100001],q[100001];
bool use[100001],vis[100001];
queue<int> qu;
inv add(int x,int y)
{
    c[++num].x=h[x];
    c[num].y=y;
    h[x]=num;
}
ini read()
{
    int x=0;
    char ch=ge;
    while(!is)
    ch=ge;
    while(is)
    {
        x=x*10+ch-48;
        ch=ge;
    }
    return x;
}
inv bfs()
{
    qu.push(1);
    q[1]=1;
    use[1]=1;
    rt[++num1]=1;
    while(!qu.empty())
    {
        int tt=qu.front();
        qu.pop();
        for(int i=h[tt];i;i=c[i].x)
          if(!use[c[i].y])
          {
              use[c[i].y]=1;
            rt[++num1]=c[i].y;
              q[c[i].y]=tt;
              qu.push(c[i].y);
          }
    }
}
inv dfs(int x,int tot)
{
    vis[x]=1;
    deep[x]=tot;
    if(!tot) return;
    for(int i=h[x];i;i=c[i].x)
      if(!vis[c[i].y]||deep[c[i].y]<deep[x]-1)
      dfs(c[i].y,tot-1);
