				}
				printf("%lld\n", ans);
			} 
		}
		for(int i = 1; i <= n; i ++) {
			sum[i] = sum[i - 1] + (a[i] * i % mod >= Min && a[i] * i % mod <= Max);
		}
		scanf("%d", &fin);
