LL n , k , d , x[MAXN] , s[MAXN] , q[MAXN] , h , t , f[MAXN] ;
bool check( LL g )
{
   LL L = max( (LL)1 , d-g ) , R = d+g , whe=1 ; 
   for(int i=1; i<=n; i++) f[i] = -INF ;
   h = 1 ; t = 0 ; 
   for(int i=1; i<=n; i++)
   {
      if( x[i] > R ) break;
      if( x[i] < L ) continue ;
      f[i] = s[i] ; 
