	    if( x[whe-1] < x[i]-R ) continue ; 
	    while( h<=t && f[q[t]] <= f[whe-1] ) t-- ; 
	    q[++t] = whe-1 ; 
      }
      if( h<=t )  f[i] =  max( f[i] , f[q[h]]+s[i] )   ; 
      if( f[i] >= k ) return 1 ; 
