  int m=(l+r)/2;                          //二分的标准程序 
  for (l;l<=r;m=(l+r)/2) {                //二分 
   	if (check(m)) r=m-1,ans=m; 
	else l=m+1; 
  }
  printf("%d\n",ans);   //输出答案，所有都不满足时，ans没被赋值，原值为-1           
  fclose(stdin); fclose(stdout);
  return 0;             //拜拜
}
